#define _CRT_SECURE_NO_WARNINGS 1
#include <stdio.h>
#include <stdlib.h>
#include <assert.h>

typedef char STDataType;
typedef struct Stack
{
	STDataType* arr;
	int top;
	int capacity;
}Stack;

// 初始化栈
void StackInit(Stack* ps);
// 入栈
void StackPush(Stack* ps, STDataType data);
// 出栈
void StackPop(Stack* ps);
// 获取栈顶元素
STDataType StackTop(Stack* ps);
// 获取栈中有效元素个数
int StackSize(Stack* ps);
// 检测栈是否为空，如果为空返回非零结果，如果不为空返回0 
int StackEmpty(Stack* ps);
// 销毁栈
void StackDestroy(Stack* ps);

// 初始化栈
void StackInit(Stack* ps)
{
	assert(ps);

	ps->capacity = 0;
	ps->top = 0;
	ps->arr = NULL;
}


// 入栈
void StackPush(Stack* ps, STDataType data)
{
	assert(ps && data);

	if (ps->top == ps->capacity)
	{
		size_t newcapacity = ps->capacity == 0 ? 4 : ps->capacity * 2;
		ps->arr = (STDataType*)realloc(ps->arr, sizeof(STDataType)*newcapacity);
		ps->capacity = newcapacity;
	}

	ps->arr[ps->top++] = data;
}
// 出栈
void StackPop(Stack* ps)
{
	assert(ps);

	if (ps->top == 0)
	{
		return;
	}

	ps->top--;
}
// 获取栈顶元素
STDataType StackTop(Stack* ps)
{
	assert(ps);

	if (ps->top == 0)
	{
		return 0;
	}

	return ps->arr[ps->top - 1];
}

// 获取栈中有效元素个数
int StackSize(Stack* ps)
{
	assert(ps);

	return ps->top;
}

// 检测栈是否为空，如果为空返回非零结果，如果不为空返回0 
int StackEmpty(Stack* ps)
{
	assert(ps);

	return ps->top == 0 ? 1 : 0;
}

// 销毁栈
void StackDestroy(Stack* ps)
{
	assert(ps);

	free(ps);
	ps->arr = NULL;
	ps->top = ps->capacity = 0;
}

int isValid(char * s){
	Stack* st = (Stack*)malloc(sizeof(Stack));

	StackInit(st);

	while (*s)
	{
		if (*s == '(' || *s == '{' || *s == '[')
		{
			StackPush(st, *s);
			s++;
		}
		else
		{
			char ch = StackTop(st);
			if (ch == '('&&*s == ')')
			{
				s++;
			}
			else if (ch == '{'&&*s == '}')
			{
				s++;
			}
			else if (ch == '['&&*s == ']')
			{
				s++;
			}
			else
			{
				return -1;
			}
			StackPop(st);
		}
	}

	if (StackEmpty(st))
	{
		return 1;
	}
	return -1;
}

